import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function Profile() {
  const [favorites, setFavorites] = useState([]);
  const [diary, setDiary] = useState([]);

  useEffect(() => {
    const load = async () => {
      try {
        const f = await api.get('/users/favorites');
        setFavorites(f.data || []);
      } catch (err) { console.warn('fav load', err); }
      try {
        const d = await api.get('/diary/me');
        setDiary(d.data || []);
      } catch (err) { console.warn('diary load', err); }
    };
    load();
  }, []);

  return (
    <div style={{ padding: 16 }}>
      <h2>Your Profile</h2>
      <section style={{ marginBottom: 20 }}>
        <h3>Favorites</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(160px,1fr))', gap: 12 }}>
          {favorites.map(t => (
            <div key={t._id} style={{ background: 'var(--light-bg)', padding: 8, borderRadius: 8 }}>
              <img src={t.coverImage} alt="cover" style={{ width: '100%', height: 100, objectFit: 'cover', borderRadius: 6 }} />
              <div style={{ marginTop: 8 }}>
                <div style={{ fontWeight: 'bold' }}>{t.title}</div>
                <div style={{ color: '#b3b3b3' }}>{t.artist}</div>
              </div>
            </div>
          ))}
        </div>
      </section>

      <section>
        <h3>Diary Entries</h3>
        <ul>
          {diary.map(e => (
            <li key={e._id} style={{ marginBottom: 8 }}>
              <strong>{e.mood}</strong> â€” {new Date(e.createdAt).toLocaleString()}
              <div>{e.text}</div>
              <div style={{ color: '#b3b3b3' }}>{e.track?.title || 'No track'}</div>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
