import React, { useState } from 'react';
import api from '../services/api';

export default function Search() {
  const [q, setQ] = useState('');
  const [results, setResults] = useState([]);

  const search = async (e) => {
    e?.preventDefault();
    try {
      const { data } = await api.get('/tracks/search', { params: { q } });
      setResults(data);
    } catch (err) {
      alert('Search failed');
    }
  };

  const save = async (item) => {
    try {
      await api.post('/tracks', item);
      alert('Saved to library');
    } catch (err) { alert('Save failed'); }
  };

  return (
    <div className="card">
      <h3>Search FMA</h3>
      <form onSubmit={search}>
        <input value={q} onChange={e=>setQ(e.target.value)} placeholder="Search tracks or artists" />
        <button type="submit">Search</button>
      </form>
      <ul className="results">
        {results.map(r=> (
          <li key={r.fmaId || r.url}>
            <div>{r.title} â€” {r.artist}</div>
            <div className="actions">
              {r.url ? <a href={r.url} target="_blank" rel="noreferrer">Stream</a> : null}
              <button onClick={()=>save(r)}>Save</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
