import React, { useState, useEffect } from 'react';
import api from '../services/api';

export default function Search() {
  const [q, setQ] = useState('');
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadTracks();
  }, []);

  const loadTracks = async () => {
    try {
      const { data } = await api.get('/tracks');
      setTracks(data);
      setLoading(false);
    } catch (err) {
      console.error('Failed to load tracks:', err);
      setLoading(false);
    }
  };

  const search = async (e) => {
    e?.preventDefault();
    try {
      const { data } = await api.get('/tracks/search', { params: { q } });
      setTracks(data);
    } catch (err) {
      console.error('Search failed:', err);
    }
  };

  const save = async (track) => {
    try {
      await api.post('/tracks', track);
      alert('Added to your library!');
    } catch (err) {
      alert('Failed to add to library');
    }
  };

  const filteredTracks = q 
    ? tracks.filter(track => 
        track.title.toLowerCase().includes(q.toLowerCase()) ||
        track.artist.toLowerCase().includes(q.toLowerCase()) ||
        track.genre.toLowerCase().includes(q.toLowerCase())
      )
    : tracks;

  if (loading) {
    return <div className="search-box">Loading...</div>;
  }

  return (
    <div>
      <div className="search-box">
        <h2>Discover Music</h2>
        <form onSubmit={search}>
          <input 
            value={q} 
            onChange={e => setQ(e.target.value)}
            placeholder="Search by title, artist, or genre"
          />
        </form>
      </div>
      
      <div className="track-grid">
        {filteredTracks.map(track => (
          <div key={track.url} className="track-card">
            <img 
              src={track.coverImage} 
              alt={track.title}
              className="track-image"
            />
            <div className="track-info">
              <h3 className="track-title">{track.title}</h3>
              <p className="track-artist">{track.artist}</p>
              <p className="track-genre">{track.genre} â€¢ {track.duration}</p>
              <div className="track-controls">
                <audio controls src={track.url} />
                <button onClick={() => save(track)}>Add to Library</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
